import { Component } from '@angular/core';





@Component({
  selector: 'app-heroe',
  templateUrl: 'heroe.component.html'
 
})
export class HeroeComponent{

      nombre: string = 'Ironman';
      edad: number = 45;


      obtnerNombre() : string {
         return `${ this.nombre } - ${ this.edad }`;
         //return  this.nombre + ' - ' this.edad.toString()

      }

}
